package ch03;

public class LiteralExam2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//변수에 직접 입력한 값;
		int x= 34;//리터럴 : 34
		double y = 42.195;//리터럴 : 42.195
		char c = '%'; //리터럴 : '%'
		boolean b = true; //리터럴 true
		String message = "hello"; // 리터럴 "hello"
		
		double result = x+ y; // 변수 만 존재
		//=> double타입 변수 result를 선언하고 변수x의 값과 변수y의 값을 더한 결과를 대입한 명령문
		
		x= 20; // 리터럴 - 직접입력한 값
		y = 3.14; // 리터럴 직접 입력한 값
	}

}
