package ch09;

public class DataTypeScope {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Wrapper클래스 - 기본타입을 특정용도(값 출력, 변경)로 만든 클래스
		System.out.println(Integer.MIN_VALUE+"<=int범위<="+ Integer.MAX_VALUE);
		System.out.println(Long.MIN_VALUE+"<=int범위<="+ Long.MAX_VALUE);
		System.out.println(Byte.MIN_VALUE+"<=int범위<="+ Byte.MAX_VALUE);
		System.out.println(Short.MIN_VALUE+"<=int범위<="+ Short.MAX_VALUE);
		System.out.println(Float.MIN_VALUE+"<=int범위<="+ Float.MAX_VALUE);
		System.out.println(Double.MIN_VALUE+"<=int범위<="+ Double.MAX_VALUE);
		

	}

}
